export function menu({children, components, ...props}) {
	const A = components.a
	return (
		<nav data-theme="dark” class="container”>
			<ul>
				<li><strong><A href=”/8547dd0d9e18438f8bf2f5d1d61bdb69”>Poko</A></strong></li>
			</ul>
			<ul>
				<li><A href="/a6613a59259042ddbafb56f50be2c013">Blog</A></li>
				<li><A href="/a234584e99da4f5c8a23a4c3ef9ca545">Articles</A></li>
				<li><A href="/102bf5d3f4d34029997e9dfebc090b41">Super</A></li>
			</ul>
		</nav>
)}

// default Layout
export function Layout({ children, components, ...props }) {
    return (
      <>
        {components.menu && <components.menu {...{ components, ...props }} />}
        {components.header && (
          <components.header {...{ components, ...props }} />
        )}
        {components.main ? (
          <components.main {...{ components, ...props }}>
            {children}
          </components.main>
        ) : (
          <main>{children}</main>
        )}
        {components.footer && <components.footer />}
      </>
    );
  }

